// search.js
async function searchProduct(searchTerm) {
    try {
      const response = await fetch(`http://localhost:5050/api/search?query=${searchTerm}`);
      if (!response.ok) {
        const errorMessage = `Error: ${response.status} ${response.statusText}`;
        console.error(errorMessage);
        return;
      }
  
      const searchResults = await response.json();
      // Process search results or update state in the parent component
      // For example, setSearchResults(searchResults) in the parent component
      console.log("Search results:", searchResults);
    } catch (error) {
      console.error("Error searching:", error);
    }
  }
  
  export default searchProduct;
