import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const InventoryItem = (props) => (
  <tr>
    <td>{props.stock.product.productDesc}</td>
    <td>{props.stock.product.productCat}</td>
    <td>{props.stock.stockQty}</td>
    <td>{props.stock.stockExp}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${props.stock._id}`}>
        Edit
      </Link>{" "}
      |
      <button
        className="btn btn-link"
        onClick={() => {
          props.deleteStock(props.stock._id);
        }}
      >
        Delete
      </button>
    </td>
  </tr>
);

export default function InventoryList() {
  const [inventory, setStock] = useState([]);

  useEffect(() => {
    async function getStock() {
      try {
        const response = await fetch(`http://localhost:5050/stockDB/`);

        if (!response.ok) {
          const message = `An error occurred: ${response.statusText}`;
          window.alert(message);
          return;
        }

        const stockList = await response.json();

        const stockWithProductDetails = await Promise.all(
          stockList.map(async (stockItem) => {
            const productResponse = await fetch(
              `http://localhost:5050/productDB/productList/${stockItem.productId}`
            );
            if (productResponse.ok) {
              const product = await productResponse.json();
              return { ...stockItem, product };
            } else {
              return stockItem;
            }
          })
        );

        setStock(stockWithProductDetails);
      } catch (error) {
        console.error("An error occurred while fetching stock:", error);
      }
    }

    getStock();
  }, []);

  async function deleteStock(id) {
    await fetch(`http://localhost:5050/stockDB/${id}`, {
      method: "DELETE",
    });

    const newStock = inventory.filter((el) => el._id !== id);
    setStock(newStock);
  }

  function inventoryList() {
    return inventory.map((stock) => {
      return (
        <InventoryItem
          stock={stock}
          deleteStock={() => deleteStock(stock._id)}
          key={stock._id}
        />
      );
    });
  }

  return (
    <div className="crudStyle">
      <h3>Inventory List</h3>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>Product</th>
            <th>Category</th>
            <th>Quantity</th>
            <th>EXP/BBE</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody className="invListStyle">{inventoryList()}</tbody>
      </table>
    </div>
  );
}
