//Import
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";


//InventoryItem card displays product in rows
const ProductItem = (props) => (
 <tr>
   <td>{props.product.productDesc}</td>
   <td>{props.product.productCat}</td>
   <td>{props.product.productQty}</td>
   <td>{props.product.productExp}</td>
   <td>
     <Link className="btn btn-link" to={`/edit/${props.product._id}`}>Edit</Link> |
     <button className="btn btn-link"
       onClick={() => {
         props.deleteProduct(props.product._id);
       }}
     >
       Delete
     </button>
   </td>
 </tr>
);

const productItem = (productProps) => (
  <tr>
    <td>{productProps.product.productDesc}</td>
    <td>{productProps.product.productCat}</td>
    <td>{productProps.product.productQty}</td>
    <td>{productProps.product.productExp}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${productProps.product._id}`}>Edit</Link> |
      <button className="btn btn-link"
        onClick={() => {
          productProps.deleteProduct(productProps.product._id);
        }}
      >
        Delete
      </button>
    </td>
  </tr>
 );

//InventoryList function to pull inventory list from DB
export default function ProductList() {
 const [inventory, setProduct] = useState([]);

 // This method fetches the inventory from the database.
 useEffect(() => {
   async function getProduct() {
     const response = await fetch(`http://localhost:5050/productDB/`);

     if (!response.ok) {
       const message = `An error occurred: ${response.statusText}`;
       window.alert(message);
       return;
     }

     const inventory = await response.json();
     setProduct(inventory);
   }

   getProduct();

   return;
 }, [inventory.length]);

 // This method will delete a product
 async function deleteProduct(id) {
   await fetch(`http://localhost:5050/productDB/${id}`, {
     method: "DELETE"
   });

   const newProduct = inventory.filter((el) => el._id !== id);
   setProduct(newProduct);
 }

 // This method will map out the product on the table
 function productList() {
   return inventory.map((product) => {
     return (
       <ProductItem
         product={product}
         deleteProduct={() => deleteProduct(product._id)}
         key={product._id}
       />
     );
   });
 }

 // This following section will display the table with the inventory of product.
 return (
   <div className="crudStyle">
     <h3>Inventory List</h3>
     <table className="table table-striped" style={{ marginTop: 20 }}>
       <thead>
         <tr>
           <th>Product</th>
           <th>Category</th>
           <th>Quantity</th>
           <th>EXP/BBE</th>
           <th>Action</th>
         </tr>
       </thead>
       <tbody className="invListStyle">{productList()}</tbody>
     </table>
   </div>
 );
}
