//Imports
import React, { useState } from "react";
//import { useNavigate } from "react-router"; (1)


//Create Stock Form Function
export default function Create() {
 const [form, setForm] = useState({
   productDesc: "",
   productExp: "",
   productCat: "",
   productQty: "",
 });
 //const navigate = useNavigate(); (1)

 // These methods will update the state properties.
 function updateForm(value) {
   return setForm((prev) => {
     return { ...prev, ...value };
   });
 }

 // This function will handle the submission.
 async function onSubmit(e) {
   e.preventDefault();

   // When a post request is sent to the create url, we'll add a new stock to the database.
   const newPerson = { ...form };

   await fetch("http://localhost:5050/stockDB", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newPerson),
   })
   .catch(error => {
     window.alert(error);
     return;
   });
   setForm({ productDesc: "", productExp: "", productCat: "", productQty: ""});
// navigate("/"); (1)
  }

 // This following section will display the form that takes the input from the user.
 return (
   <div className="crudCreateStyle">
     <h3>Create New Stock</h3>
     <form onSubmit={onSubmit} className="formStyle">
       <div className="form-group">
         <label htmlFor="productDesc">Product</label>
         <input
           type="text"
           className="form-control"
           id="productDesc"
           value={form.productDesc}
           onChange={(e) => updateForm({ productDesc: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="productExp">Expiry/Best Before</label>
         <input
           type="date"
           className="form-control"
           id="productExp"
           value={form.productExp}
           onChange={(e) => updateForm({ productExp: e.target.value })}
         />
       </div>
       <label>Product Category</label>
       <div className="form-group">
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="categoryOptions"
             id="categoryDairy"
             value="Dairy"
             checked={form.productCat === "Dairy"}
             onChange={(e) => updateForm({ productCat: e.target.value })}
           />
           <label htmlFor="categoryDairy" className="form-check-label">Dairy</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="categoryOptions"
             id="categoryMeat"
             value="Meat"
             checked={form.productCat === "Meat"}
             onChange={(e) => updateForm({ productCat: e.target.value })}
           />
           <label htmlFor="categoryMeat" className="form-check-label">Meat</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="categoryOptions"
             id="categoryVeg"
             value="Veg"
             checked={form.productCat === "Veg"}
             onChange={(e) => updateForm({ productCat: e.target.value })}
           />
           <label htmlFor="categoryVeg" className="form-check-label">Veg</label>
         </div>
       </div>
       <div className="form-group">
         <input
           type="submit"
           value="Add stock"
           className="btn btn-primary"
         />
       </div>
     </form>
   </div>
 );
}
