import React, { useState } from "react";

export default function CreateProduct() {
  const [form, setForm] = useState({
    productDesc: "",
    productCat: "",
  });

  async function onSubmit(e) {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5050/productDB", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(form),
      });

      if (response.ok) {
        console.log("Product added successfully!");
        setForm({ productDesc: "", productCat: "" });
      } else {
        console.error("Failed to add product.");
      }
    } catch (error) {
      console.error("An error occurred:", error);
    }
  }

  return (
    <div className="create-product">
      <h3>Create New Product</h3>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="productDesc">Product Name</label>
          <input
            type="text"
            id="productDesc"
            value={form.productDesc}
            onChange={(e) =>
              setForm({ ...form, productDesc: e.target.value })
            }
          />
        </div>
        <div className="form-group">
          <label htmlFor="productCat">Product Category</label>
          <input
            type="text"
            id="productCat"
            value={form.productCat}
            onChange={(e) =>
              setForm({ ...form, productCat: e.target.value })
            }
          />
        </div>
        <button type="submit">Add Product</button>
      </form>
    </div>
  );
}
