//Imports
import React, { useState } from "react";
import searchProduct from "./search.js";
import { useNavigate } from "react-router-dom";

// BootStrap Imports
import "bootstrap/dist/css/bootstrap.css";
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';

//NavLink to utilize the react router.
import { NavLink } from "react-router-dom";

// Function to display navbar
export default function Menu() {
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    searchProduct(searchTerm); // Call the searchProduct function with the search term
  };


  return (
    <Navbar bg="light" data-bs-theme="light" expand="lg" className="bg-body-tertiary justify-content-between ">
      <Container fluid>

        <Navbar.Brand href="/">Househole Inventory App</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <NavLink className="nav-link" to="/create">
               Create Record
             </NavLink>
            <NavDropdown title="Categories" id="navbarScrollingDropdown">
              <NavDropdown.Item href="#action3">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action4">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action5">
                Something else here
              </NavDropdown.Item>
            </NavDropdown>
          </Nav>
          <Form className="d-flex" onSubmit={handleSearchSubmit}>
      <Form.Control
        type="search"
        value={searchTerm}
        onChange={handleSearchChange}
        className="me-2"
        aria-label="Search"
        placeholder="Search Stock"
      />
      <Button type="submit" variant="outline-success">
        Search
      </Button>
    </Form>
        </Navbar.Collapse>
        </Container>
    </Navbar>
  );
}
